import React, { Component } from 'react'
import { Field, reduxForm } from 'redux-form';

class UserForm extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         mode: 'signup'
      }
    }
    
    renderInput = ({ input, label, type, meta: { touched, error } }) => {
        return (
            <div className="form-group">
                <label htmlFor={input.name}>{label}</label>
                <input {...input} className="form-control" type={type} placeholder={label} />
                {touched && error && <span className="help-block">{error}</span>}
            </div>
        )
    }

    changeMode = () => {
        this.setState(state => {
            if(state.mode === 'signup') return {...state, mode: 'login'}
            else return {...state, mode: 'signup'}
        });
    }

    render() {
        return (
            <div className="user-form">
                <h5>{this.state.mode === 'signup' ? 'Create new account' : 'Login to your account'}</h5>
                <form onSubmit={this.props.handleSubmit} noValidate>
                    <Field
                        name="email"
                        type="email"
                        label="Email"
                        component={this.renderInput}
                    />
                    <Field
                        name="password"
                        type="password"
                        label="Password"
                        component={this.renderInput}
                    />
                    <button type="submit" className="btn btn-primary">{this.state.mode === 'signup' ? 'SIGN UP' : 'LOG IN'}</button>
                    <small onClick={() => this.changeMode()} className="text-muted">{this.state.mode === 'signup' ? 'LOG IN' : 'SIGN UP' }</small>
                </form>
            </div>
        )
    }
}

export default reduxForm({
    form: 'userForm'
})(UserForm);
