import React, { Component } from 'react';

import Header from './layout/Header';
import Footer from './layout/Footer';

import BuildForm from './containers/BuildForm';

const SassGenerator = variables => {
    return `
    $primary: ${variables.primary};
    $secondary: ${variables.secondary};
    $name: ${variables.bgName};
    $bg: ${variables.bgColor};
    $enable-inverse: ${variables.enableInverse};
    $enable-darken: ${variables.enableDarken};
    $enable-lighten: ${variables.enableLighten};
    $enable-contrast: ${variables.enableContrast};
    $enable-trans: ${variables.enableTrans};
    $enable-gray: ${variables.enableGray};

    @import 'node_modules/wi-framework/index';
    ${variables.components === "all" ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.navbar ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.carousel ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.alert ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.badge ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.breadcrumb ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.buttonGroup ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.buttons ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.card ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.code ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.customForms ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.forms ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.dropdown ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.inputGroup ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.jumbotron ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.listGroup ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.media ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.modal ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.nav ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.pagination ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.popover ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.progress ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.tables ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    ${variables.tooltip ? "@import 'node_modules/wi-framework/bootstrap/components/all';" : ""}
    `
}

class BuildTool extends Component
{
    submitHandler = values => {
        console.log(SassGenerator(values).trim());
    }

    render() {
        return (
            <div className="bg-dark">
                <Header />
                <section className="build-tool-section">
                    <div className="container-fluid">
                        <h2>Build Tool</h2>
                        <hr/>
                        <div className="build-colors">
                            <BuildForm onSubmit={this.submitHandler} />
                        </div>
                    </div>
                </section>
                <Footer />
            </div>
        )
    }
}

export default BuildTool;
