import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Components from '../components/Components';
import styled from 'styled-components';
import { connect } from 'react-redux';

class TemplatePreview extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         variables: props.variables,
         shapesList: props.shapesList,
         result: props.result
      }
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
            variables: nextProps.variables,
            shapesList: nextProps.shapesList,
            result: nextProps.result
        })
    }
    
    shouldComponentUpdate(nextProps) {
        if(this.props.variables !== nextProps.variables) {            
            return true;
        }
        if(this.props.result !== nextProps.result) {            
            return true;
        }
        return false;
    }
    
    render() {
        let variables = this.state.variables ? this.state.variables : [];
        let shapesList = this.state.shapesList ? this.state.shapesList : [];
        
        // Array of shapes
        let varsArray = Object.keys(variables);
        let shapesArray = [];
        if(variables.shapes !== 'all') {
            varsArray.forEach(element => {
                if(variables[element] === true && !element.startsWith('enable')) {
                    shapesArray.push(element);
                }
            });
        }
        console.log(this.state.result);
        
        // Style
        const Template = styled.div`${this.state.result ? this.state.result.bg : ''}`;

        return (
            <Template>
                <div className={`template_preview bg-${variables.name}${this.state.variables ? '' : ' hidden'}`}>
                    {
                        shapesList.length > 0 && shapesList.map(component => {
                            return component.shapes.length > 0 && component.shapes.map(shape => {
                                if(variables.shapes === 'all') {
                                    return (
                                        <Components
                                            key={shape.shape}
                                            component={component.name}
                                            shape={shape.shape}
                                        />
                                    )
                                }
                                else {
                                    return shapesArray.map(item => {
                                        if (item === shape.shape) {
                                            return (
                                                <Components
                                                    key={item}
                                                    component={component.name}
                                                    shape={shape.shape}
                                                />
                                            )
                                        }
                                        else {
                                            return null;
                                        }
                                    });
                                }
                            })
                        })
                    }
                    <Link to="/template" target="_blank">See in new page ></Link>
                </div>
            </Template>
        )
    }
}


const mapStateToProps = state => {    
    return {
        variables: state.compile.variables,
        shapesList: state.shapes.shapesList,
        result: state.compile.result
    }
}

export default connect(mapStateToProps)(TemplatePreview);
