import React, { Component } from 'react';
import { connect } from 'react-redux';

class Admin extends Component
{
    render() {
        return (
            <div className="admin">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">name</th>
                            <th scope="col">component</th>
                            <th scope="col">codepen</th>
                            <th scope="col">status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.shapesList && (
                            this.props.shapesList.map((component) => {
                                component.shapes && (
                                    component.shapes.map((shape, index) => {
                                        return (
                                            <tr key={index}>
                                                <th scope="row">{index}</th>
                                                <td>{shape.name}</td>
                                                <td>{component.name}</td>
                                                <td>{shape.codepen ? shape.codepen : 'Private'}</td>
                                                <td>{shape.status ? 'Active' : 'Inactive'}</td>
                                            </tr>
                                        )
                                    })
                                )
                            })
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    shapesList: state.shapes.shapesList
})

export default connect(mapStateToProps)(Admin);
