import React, { Component } from 'react';

import { Field, FieldArray, reduxForm } from 'redux-form';
import validate from '../components/utilities/validate';

const renderInput = ({input, label, type, meta: { touched, error }}) => {
    return (
        <div className="form-group">
            <label htmlFor={input.name}>{label}</label>
            <input {...input} type={type} placeholder={label} />
            { touched && error && <span className="help-block">{error}</span>}
        </div>
    )
}

const renderCheck = ({input, label, type, meta: { touched, error }}) => {
    return (
        <div className="form-check">
            <input className="form-check-input" {...input} type={type} placeholder={label} />
            <label className="form-check-label" htmlFor={input.name}>{label}</label>
            { touched && error && <span className="help-block">{error}</span>}
        </div>
    )
}

const BuildForm = props => {
    const { handleSubmit, pristine, reset, submitting } = props;
    return (
        <form onSubmit={handleSubmit}>
            <h4>Add main colors</h4>
            <Field
                name="PrimaryColor"
                type="color"
                component={renderInput}
                label="Primary Color"
            />
            <Field
                name="SecondaryColor"
                type="color"
                component={renderInput}
                label="Secondary Color"
            />
            <h4>Create background</h4>
            <Field
                name="BackgroundName"
                type="text"
                component={renderInput}
                label="Background Name"
            />
            <Field
                name="BackgroundColor"
                type="color"
                component={renderInput}
                label="Background Color"
            />
            <h4>Import Bootstrap components</h4>
            <Field
                name="all"
                type="checkbox"
                component={renderCheck}
                label="All components"
            />
        </form>
    )
}

export default reduxForm({
    form: 'buildForm',
    validate
})(BuildForm);
