import React, { Component } from 'react';

import Header from './layout/Header';
import Footer from './layout/Footer';

import Highlight from 'react-highlight';
import BuildForm from './containers/BuildForm';

import { connect } from 'react-redux';
import { changeBackground } from './actions';

class BuildTool extends Component
{
    submitHandler = values => {
        console.log(this.props);
        
        this.props.sendVariables(values);
    }

    render() {
        return (
            <div className="bg-dark">
                <Header />
                <section className="build-tool-section">
                    <div className="container-fluid">
                        <h2>Build Tool</h2>
                        <hr/>
                        <div className="build-colors">
                            <BuildForm onSubmit={this.submitHandler} />
                        </div>
                        <div className="build-result">
                            <Highlight className={`css ${this.props.result ? '' : 'hidden'}`}>
                                {this.props.result && this.props.result.css.data}
                            </Highlight>
                        </div>
                    </div>
                </section>
                <Footer />
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        error: state.backgroundReducer.error,
        result: state.backgroundReducer.result,
        variables: state.backgroundReducer.variables
    }
}

const mapDispatchToProps = dispatch => {
    return {
        sendVariables: vars => dispatch(changeBackground(vars))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BuildTool);
