module.exports = (params, full = true) => {
    let sass = '';
    let map = '';
    if(params.primary) sass += `$primary: ${params.primary};`;
    if(params.secondary) sass += `$secondary: ${params.secondary};`;
    if(params.enable_default_root) `$enable-default-root: ${params.enable_default_root};`;
    if(params.enable_inverse) `$enable-inverse: ${params.enable_inverse};`;
    if(params.enable_contrast) `$enable-contrast: ${params.enable_contrast};`;
    if(params.enable_trans) `$enable-trans: ${params.enable_trans};`;
    if(params.enable_gray) `$enable-gray: ${params.enable_gray};`;
    if(params.enable_darken) `$enable-darken: ${params.enable_darken};`;
    if(params.enable_lighten) `$enable-lighten: ${params.enable_lighten};`;
    if(params.text || params.headers || params.links) {
        map += ',(';
            if(params.text) map += `'text': ${params.text},`;
            if(params.headers) map += `'headers': ${params.headers},`;
            if(params.links) map += `'links': ${params.links},`;
        map += ')';
    }
    if(params.background && params.name && full) {
        sass += `@import 'node_modules/wi-framework/variables/index';`;
        sass += `@import 'node_modules/wi-framework/colors/index';`;
        sass += `@include create-background('${params.name}', ${params.background}${map});`;
    }
    if(!full) {
        sass += `@import 'node_modules/wi-framework/index';`;
    }

    return sass;
}
