import React, { Component } from 'react';
import Header from '../layout/Header';
import Footer from '../layout/Footer';
import ShaperForm from './ShaperForm';
import Components from '../components/Components';

class Shaper extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            compPreview: '',
            shapePreview: '',
            codepen: 'https://codepen.io/waelzoaiter/pen/wRprGP/'
        }
    }

    submitHandler = values => {
        console.log(values);
        this.setState({
            compPreview: values.component_name,
            shapePreview: values.shape_name,
            codepen: values.codepen
        });
    }

    render() {
        return (
            <div className="shaper">
                <Header image="webinjaz-shaper.png" current="Shaper" version="v0.0.1(beta)" />
                <ShaperForm onSubmit={this.submitHandler} />
                    {/* <Components component={this.state.compPreview} shape={this.state.shapePreview} /> */}
                    <p 
                        data-height="375" 
                        data-theme-id="0" 
                        data-slug-hash="wRprGP" 
                        data-default-tab="html,result" 
                        data-user="waelzoaiter" 
                        data-pen-title="Empty" 
                        className="codepen"
                        >See the Pen <a href={this.state.codepen}>Empty</a> by Wael Zoaiter (<a href="https://codepen.io/waelzoaiter">@waelzoaiter</a>) on <a href="https://codepen.io">CodePen</a>.
                    </p>
                <Footer />
            </div>
        )
    }
}

export default Shaper;
