import React, { Component } from 'react';

import Header from '../layout/Header';
import Footer from '../layout/Footer';

import StylerForm from './StylerForm';

import { connect } from 'react-redux';
import { compileToCss } from '../actions';

import ColorsPreview from '../components/ColorsPreview';
import CombileResult from '../components/CombileResult';
import TemplatePreview from './TemplatePreview';

class Styler extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         variables: {},
         isFullScreen: false
      }
    }
    
    submitHandler = values => {
        this.props.sendVariables(values);
        this.setState({
            variables: values
        });
    }

    shouldComponentUpdate(nextProps) {
        if (this.props.variables !== nextProps.variables ||
            this.props.result !== nextProps.result) {
            return true;
        }
        return false;
    }

    fullscreenHandler = () => {
        this.setState(prevState => ({
            ...prevState,
            isFullScreen: !prevState.isFullScreen
        }))
    }

    render() {
        const hide = this.state.isFullScreen ? 'hidden' : '';
        return (
            <div className="bg-dark">
                <Header className={hide} image="webinjaz-styler.png" current="Styler" version="v1.0.0(beta)" />
                <section className="build-tool-section">
                    <StylerForm className={hide} onSubmit={this.submitHandler} />
                    <span onClick={() => this.fullscreenHandler}>full</span>
                    <TemplatePreview variables={this.props.variables} result={this.props.result} shapesList={this.props.shapesList} />
                    <CombileResult className={hide} result={this.props.result} error={this.props.error} />
                    <ColorsPreview className={hide} variables={this.props.variables} result={this.props.result} />
                </section>
                <Footer className={hide} />
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        error: state.compile.error,
        result: state.compile.result,
        variables: state.compile.variables,
        shapesList: state.shapes.shapesList
    }
}

const mapDispatchToProps = dispatch => {
    return {
        sendVariables: vars => dispatch(compileToCss(vars))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Styler);
