import React, { Component } from 'react';
import { MDBTabPane, MDBTabContent, MDBNav, MDBNavItem, MDBNavLink } from
"mdbreact";
import Highlight from 'react-highlight';

class Result extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activeItem: 'sass'
        }
    }

    toggle = tab => {
        if (this.state.activeItem !== tab) {
            this.setState({
                activeItem: tab
            });
        }
    }

    render() {
        return (
            <div className="form-section">
                <h4>Results</h4>
                <MDBNav tabs className="nav-justified">
                    <MDBNavItem>
                        <MDBNavLink 
                            to="" 
                            className={this.state.activeItem === 'sass' ? 'active' : ''} 
                            onClick={() => this.toggle('sass')}
                            role="tab"
                            >Sass</MDBNavLink>
                    </MDBNavItem>
                    <MDBNavItem>
                        <MDBNavLink 
                            to="" 
                            className={this.state.activeItem === 'css' ? 'active' : ''} 
                            onClick={() => this.toggle('css')}
                            role="tab"
                            >Css</MDBNavLink>
                    </MDBNavItem>
                </MDBNav>
                <MDBTabContent activeItem={this.state.activeItem}>
                    <MDBTabPane tabId="sass" role="tabpanel">
                        <Highlight className={`css ${this.props.sass ? '' : 'hidden'}`}>
                            {this.props.sass.replace(/;/g, ";\n")}
                        </Highlight>
                    </MDBTabPane>
                    <MDBTabPane tabId="css" role="tabpanel">
                        <Highlight className={`css ${this.props.css ? '' : 'hidden'}`}>
                            {this.props.css && this.props.css}
                        </Highlight>
                    </MDBTabPane>
                </MDBTabContent>
            </div>
        );
    }
}

export default Result;
