import React, { Component } from 'react'
import UserForm from './UserForm';
import { connect } from 'react-redux';
import { signinAction, signupAction } from '../actions';

class User extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         mode: 'signup'
      }
    }
    
    changeMode = () => {
        this.setState(state => {
            if(state.mode === 'signup') return {...state, mode: 'login'}
            else return {...state, mode: 'signup'}
        });
    }

    submitHandler = values => {
        console.log(values);
        if(this.state.mode === 'signup') 
            this.props.signupAction(values);
        else
            this.props.signinAction(values);
    }

    render() {
        console.log(this.props.userForm);
        
        return (
            <div>
                <UserForm 
                    onSubmit={this.submitHandler} 
                    mode={this.state.mode}
                    changeMode={() => this.changeMode()}
                />
            </div>
        )
    }
}

const mapStateToProps = state => ({
    userForm: state.form.userForm
});

const mapDispatchToProps = dispatch => ({
  signup: userData => dispatch(signupAction(userData)),
  signin: userData => dispatch(signinAction(userData))
});

export default connect(mapStateToProps, mapDispatchToProps)(User);
