import React, { Component } from 'react'
import Highlight from 'react-highlight';

class CombileResult extends Component {

    shouldComponentUpdate(nextProps) {
        if(this.props.result !== nextProps.result) {            
            if(this.props.result && this.props.result.sass !== nextProps.result.sass) {
                return true;
            }
        }
        return false;
    }

    render() {        
        return (
            <div className={`build-result ${this.props.result ? '' : 'hidden'}`}>
                <div className="form-section">
                    <h4>Results</h4>
                    <div className="row">
                        <div className="col-md-6 col-sm-12">
                            <span className="lang">Sass</span>
                            <Highlight className={`css `}>
                                {this.props.result && this.props.result.sass.replace(/;/g, ";\n")}
                            </Highlight>
                        </div>
                        <div className="col-md-6 col-sm-12">
                            {this.props.error && <div className="row">
                                <div className="col-12">
                                    <blockquote className="blockquote">Something went wrong: {this.props.error || this.props.error.statusText}</blockquote>
                                </div>
                            </div>}
                            <span className="lang">Css</span>
                            <Highlight className={`css ${this.props.result ? '' : 'hidden'}`}>
                                {this.props.result && this.props.result.css}
                            </Highlight>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default CombileResult;
